import React from 'react';

function TaskItem({ task, onDelete, onToggle, onEdit }) {
  let badgeStyle = {};
  let ball = '';
  if (task.priority === 1) {
    badgeStyle = { background: '#ffe5ec', color: '#c62828', fontWeight: 600, width: 18, height: 18, borderRadius: '50%', display: 'inline-block', marginLeft: 8 };
    ball = ' ';
  } else if (task.priority === 2) {
    badgeStyle = { background: '#fffde7', color: '#f57f17', fontWeight: 600, width: 18, height: 18, borderRadius: '50%', display: 'inline-block', marginLeft: 8 };
    ball = ' ';
  } else {
    badgeStyle = { background: '#e0f7fa', color: '#388e3c', fontWeight: 600, width: 18, height: 18, borderRadius: '50%', display: 'inline-block', marginLeft: 8 };
    ball = ' ';
  }

  // Set the background color for the ball
  if (task.priority === 1) badgeStyle.background = '#ff5252';
  if (task.priority === 2) badgeStyle.background = '#ffb300';
  if (task.priority === 3) badgeStyle.background = '#43a047';

  return (
    <li className={`task-item${task.completed ? ' completed' : ''}`}>
      <div className="task-main">
        <input
          type="checkbox"
          checked={task.completed}
          onChange={() => onToggle(task)}
        />
        <span style={{ display: 'flex', alignItems: 'center', gap: '0.7rem' }}>
          {/* Only show checkmark if not already completed */}
          {!task.completed && <span>{task.task}</span>}
          {task.completed && <span style={{color: '#22c55e', fontWeight: 700, fontSize: '1.2em'}}>{task.task}</span>}
          <span className="priority-badge" style={badgeStyle}></span>
        </span>
      </div>
      <div className="task-actions">
        <button className="delete-btn" onClick={() => onDelete(task.id)}>Delete</button>
        <button className="edit-btn" onClick={() => {
          const newTask = prompt('Edit task:', task.task) || task.task;
          const newPriority = prompt('Edit priority (1=High, 2=Medium, 3=Low):', task.priority) || task.priority;
          onEdit(task, { ...task, task: newTask.trim(), priority: Number(newPriority) });
        }}>Edit</button>
      </div>
    </li>
  );
}

export default TaskItem;
