import React from 'react';

function formatDate(dateStr) {
  if (!dateStr) return '';
  const d = new Date(dateStr);
  if (isNaN(d)) return '';
  return d.toLocaleDateString('en-GB');
}

function TaskItem({ task, onDelete, onToggle, onEdit }) {
  let priorityLabel = '';
  let badgeStyle = {};
  if (task.priority === 1) {
    priorityLabel = 'ðŸ”´ High';
    badgeStyle = { background: '#ffcdd2', color: '#c62828', fontWeight: 600 };
  } else if (task.priority === 2) {
    priorityLabel = 'ðŸŸ¡ Medium';
    badgeStyle = { background: '#fff9c4', color: '#f57f17', fontWeight: 600 };
  } else {
    priorityLabel = 'ðŸŸ¢ Low';
    badgeStyle = { background: '#c8e6c9', color: '#388e3c', fontWeight: 600 };
  }

  return (
    <li className={`task-item${task.completed ? ' completed' : ''}`}>
      <div className="task-main">
        <input
          type="checkbox"
          checked={task.completed}
          onChange={() => onToggle(task)}
        />
        <span style={{ display: 'flex', alignItems: 'center', gap: '0.7rem' }}>
          {task.completed && <span style={{color: '#22c55e', fontWeight: 700, fontSize: '1.2em'}}>âœ”</span>}
          <span>{task.task}</span>
          {task.dueDate && <span style={{color: '#01579b', fontWeight: 500}}> | Due: {formatDate(task.dueDate)}</span>}
          <span style={{marginLeft: 8}}><span className="priority-badge" style={badgeStyle}>{priorityLabel}</span></span>
        </span>
      </div>
      <div className="task-actions">
        <button className="delete-btn" onClick={() => onDelete(task.id)}>Delete</button>
        <button className="edit-btn" onClick={() => {
          const newTask = prompt('Edit task:', task.task) || task.task;
          const newDueDate = prompt('Edit due date (YYYY-MM-DD):', task.dueDate || '') || task.dueDate;
          const newPriority = prompt('Edit priority (1=High, 2=Medium, 3=Low):', task.priority) || task.priority;
          onEdit(task, { ...task, task: newTask.trim(), dueDate: newDueDate, priority: Number(newPriority) });
        }}>Edit</button>
      </div>
    </li>
  );
}

export default TaskItem;
