import React, { useEffect, useState } from 'react';
import TaskForm from './TaskForm';
import TaskList from './TaskList';
import './App.css';

const API_URL = 'http://localhost:8080/api/todos';

function App() {
  const [tasks, setTasks] = useState([]);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
  }, [darkMode]);

  const fetchTasks = async () => {
    const res = await fetch(API_URL);
    const data = await res.json();
    setTasks(data);
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = async (task) => {
    await fetch(API_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ task, completed: false })
    });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await fetch(`${API_URL}/${id}`, { method: 'DELETE' });
    fetchTasks();
  };

  const toggleComplete = async (taskObj) => {
    await fetch(`${API_URL}/${taskObj.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...taskObj, completed: !taskObj.completed })
    });
    fetchTasks();
  };

  const editTask = async (taskObj, newTask) => {
    await fetch(`${API_URL}/${taskObj.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...taskObj, task: newTask })
    });
    fetchTasks();
  };

  return (
    <div className="todo-container">
      <button
        onClick={() => setDarkMode(dm => !dm)}
        style={{
          alignSelf: 'flex-end',
          marginBottom: '1rem',
          background: darkMode ? '#232526' : '#fcb6f6',
          color: darkMode ? '#fce38a' : '#6d5b7b',
          border: 'none',
          borderRadius: '8px',
          padding: '0.4rem 1rem',
          fontWeight: 600,
          cursor: 'pointer',
          transition: 'background 0.2s, color 0.2s',
        }}
        aria-label="Toggle dark mode"
      >
        {darkMode ? 'â˜€ Light Mode' : 'ðŸŒ™ Dark Mode'}
      </button>
      <h1>To Do List</h1>
      <TaskForm addTask={addTask} />
      <h2>Task List</h2>
      <TaskList tasks={tasks} deleteTask={deleteTask} toggleComplete={toggleComplete} editTask={editTask} />
      <div id="taskSummary">
        Completed: {tasks.filter(t => t.completed).length} | Uncompleted: {tasks.filter(t => !t.completed).length}
      </div>
    </div>
  );
}

export default App;
