import React from 'react';

function TaskItem({ task, onDelete, onToggle, onEdit }) {
  let badgeStyle = {
    width: 16,
    height: 16,
    borderRadius: '50%',
    display: 'inline-block',
    marginLeft: 8,
    marginRight: 0,
    verticalAlign: 'middle',
    border: '1.5px solid #e0e0e0',
  };
  if (task.priority === 1) badgeStyle.background = '#ff5252';
  if (task.priority === 2) badgeStyle.background = '#ffb300';
  if (task.priority === 3) badgeStyle.background = '#43a047';

  return (
    <li className={`task-item${task.completed ? ' completed' : ''}`} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: '1rem', width: '100%', marginBottom: '12px' }}>
      <div style={{ display: 'flex', alignItems: 'center', flex: 1, minWidth: 0, gap: '0.7rem' }}>
        <input
          type="checkbox"
          checked={task.completed}
          onChange={() => onToggle(task)}
          style={{ marginRight: 8 }}
        />
        <span style={{ display: 'flex', alignItems: 'center', minWidth: 0, gap: '0.5rem', flex: 1 }}>
          <span style={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', fontWeight: task.completed ? 700 : 500, color: task.completed ? '#22c55e' : '#333' }}>{task.task}</span>
          <span className="priority-badge" style={badgeStyle}></span>
        </span>
      </div>
      <div className="task-actions" style={{ display: 'flex', gap: '0.5rem', alignItems: 'center', justifyContent: 'flex-end', minWidth: 120 }}>
        <button className="delete-btn" onClick={() => onDelete(task.id)}>Delete</button>
        <button className="edit-btn" onClick={() => {
          const newTask = prompt('Edit task:', task.task) || task.task;
          const newPriority = prompt('Edit priority (1=High, 2=Medium, 3=Low):', task.priority) || task.priority;
          onEdit(task, { ...task, task: newTask.trim(), priority: Number(newPriority) });
        }}>Edit</button>
      </div>
    </li>
  );
}

export default TaskItem;
